import React from 'react';
import './Contact.css';
import emailjs from "emailjs-com";
import contactImg from "./img/doge-mini-removebg-preview.png"
const Contact = () => {
    const formRef = useRef();
    const [done, setDone] = useState(false)
    const theme = useContext(ThemeContext);
    const darkMode = theme.state.darkMode;
  
    const handleSubmit = (e) => {
      e.preventDefault();
      emailjs
        .sendForm(
          "service_nswbyex",
          "template_kaa7i8a",
          formRef.current,
          "user_Ld2mI0y1e16c2SzyWou9G"
        )
        .then(
          (result) => {
            console.log(result.text);
            setDone(true)
          },
          (error) => {
            console.log(error.text);
          }
        );
    };
  
    return (
      <div className="c">
        <div className="c-bg"></div>
        <div className="c-wrapper">
          <div className="c-left">
            <h1 className="c-title">Talk to me about your needs</h1>
            <div className="c-info">
              <div className="c-info-item">
                <img src={Phone} alt="" className="c-icon" />
                +1 587-966-9668 
              </div>
              <div className="c-info-item">
                <img className="c-icon" src={Email} alt="" />
                bkcsplayer@gmail.com
              </div>
              <div className="c-info-item">
                <img className="c-icon" src={Address} alt="" />
                Calgary AB
              </div>
            </div>
          </div>
          <div className="c-right">
            <p className="c-desc">
              <b>It's time to turn your ideas into App</b> Get in touch. How do you know if you don't ask?
            </p>
            <form ref={formRef} onSubmit={handleSubmit}>
              <input style={{backgroundColor: darkMode && "#333"}} type="text" placeholder="Name" name="user_name" />
              <input style={{backgroundColor: darkMode && "#333"}} type="text" placeholder="Subject" name="user_subject" />
              <input style={{backgroundColor: darkMode && "#333"}} type="text" placeholder="Email" name="user_email" />
              <textarea style={{backgroundColor: darkMode && "#333"}} rows="5" placeholder="Message" name="message" />
              <button>Submit</button>
              {done && "Thank you..."}
            </form>
          </div>
        </div>
      </div>
    );
  };
  
  export default Contact;
